package com.jbt.util;import java.text.DecimalFormat;/** * 地图模块计算工具 * @author zhangzhongde * */public class MapUtil {	public static  double bumberDouble(double number) {		DecimalFormat df = new DecimalFormat("0.00");		String numberString = df.format(number);		double numberDouble = Double.parseDouble(numberString);		return numberDouble;	}		public static String getParamTime(int time)	{		String trueTime = new String();		if(time != 0)		{			long hour,minute,second;			hour = time/3600;			minute = time/60 - hour * 60;			second = time - hour*3600 - minute*60;			if(hour > 0)			{				trueTime = String.valueOf(hour)+"小时"+String.valueOf(minute)+"分钟"+String.valueOf(second)+"秒";			}			else if(minute > 0)			{				trueTime = String.valueOf(minute)+"分钟"+String.valueOf(second)+"秒";			}			else			{				trueTime = String.valueOf(second)+"秒";			}		}		return trueTime;	}}