package com.jbt.util;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpSession;import org.springframework.web.context.request.RequestContextHolder;import org.springframework.web.context.request.ServletRequestAttributes;public class SessionUtil {		public static HttpServletRequest getRequest(){		return ((ServletRequestAttributes)RequestContextHolder.getRequestAttributes()).getRequest();	}		public static HttpSession getSession(){		return getRequest().getSession();	}		public static Integer getManagerId(){		return (Integer) getSession().getAttribute("managerId");	}		public static String getManagerName(){		return (String) getSession().getAttribute("username");	}}